const ContactForm = (): JSX.Element => {
    const {
        register,
        handleSubmit,
        reset,
        formState: { errors },
    } = useForm<ContactFormData>({
        resolver: zodResolver(contactFormSchema),
        mode: 'onChange',
    });
    const t = useTranslations('Contact.successReport');
    const [formState, dispatch] = useReducer(formStateReducer, initialState);

    const onSubmit = async (data: ContactFormData): Promise<void> => {
        dispatch({ type: actionTypes.FORM_SUBMITTED });
        const res = await contactFormSubmitHandler(data);

        if (res.success) {
            dispatch({ type: actionTypes.SUBMISSION_SUCCEEDED });
            reset();
        } else {
            dispatch({ type: actionTypes.SUBMISSION_FAILED });
        }
    };

    const focusClasses: string =
        'focus:ring-3 focus:ring-[var(--accent)] focus:ring-offset-2 focus:ring-offset-[var(--background)]';

    if (formState.isSuccess) {
        return (
            <SuccessReport
                onClickClose={() => dispatch({ type: actionTypes.FORM_RESET })}
                reportType='success'
                imageElement={
                    <SvgButton
                        onClickFunction={() =>
                            dispatch({ type: actionTypes.FORM_RESET })
                        }
                        label={t('success.svgLabel')}
                        buttonType='form-success'
                        size='xl'
                    />
                }
            />
        );
    }

    if (formState.isFailure) {
        return (
            <SuccessReport
                onClickClose={() => dispatch({ type: actionTypes.FORM_RESET })}
                reportType='failure'
                imageElement={<div className='svg-button p-2 h-50 w-50'>{maskSvg}</div>}
            ></SuccessReport>
        );
    }

    return (
        <form onSubmit={handleSubmit(onSubmit)}>
            <div className='grid gap-x-6 md:grid-cols-2'>
                <FormInput
                    inputId='name'
                    inputType='text'
                    register={register}
                    errors={errors}
                    focusClasses={focusClasses}
                />
                <FormInput
                    inputId='email'
                    inputType='email'
                    register={register}
                    errors={errors}
                    focusClasses={focusClasses}
                />
            </div>{' '}
            <FormInput
                inputId='subject'
                inputType='text'
                register={register}
                errors={errors}
                focusClasses={focusClasses}
            />
            <FormInput
                inputId='message'
                inputType='text-area'
                register={register}
                errors={errors}
                focusClasses={focusClasses}
            />
            <SubmitButton
                isSending={formState.isSending}
                focusClasses={focusClasses}
                buttonType='submit'
            />
        </form>
    );
};

export default ContactForm;
